mapper.select:

select no, title, contents, hit, date_format(reg_date, '%Y-%m-%d %h:%i:%s') as regDate, g_no as gNo, o_no as oNo, depth, user_no as userNo
from board
where no = #{no }

mapper.update:

	update board
	set hit = hit + 1
	where no = #{no}


	update board
	set title = #{title }, contents = #{contents }
	where no = #{no }
